import React, { useState, useEffect } from 'react';
import * as pdfjs from 'pdfjs-dist/build/pdf';
import 'pdfjs-dist/build/pdf.worker'; // Import the PDF.js worker

import './main.css';

export default function Main() {
    const [drag, setDrag] = useState(flase)
    return(
        <div>
            {drag
                ? <div className='drop-area'>Отпустите файлы</div>
                : <div className='drop-area'>Перетащите файлы</div>
        }
        </div>
    );
}
